import React, { useState, useEffect } from 'react';

function Carousel() {
  const slides = [
    { id: 1, image: '/images/carousel1.jpg' },
    { id: 2, image: '/images/carousel2.jpg' },
    { id: 3, image: '/images/carousel3.jpg' },
  ];

  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % slides.length);
    }, 3000); // Change slide every 3 seconds
    return () => clearInterval(interval);
  }, [slides.length]);

  return (
    <div className="bg-gray-200 py-8">
      <div className="container mx-auto px-4">
        <h2 className="text-xl font-bold mb-4">Special Offers</h2>
        <div className="relative overflow-hidden rounded-lg shadow-lg">
          <div
            className="flex transition-transform duration-500 ease-in-out"
            style={{ transform: `translateX(-${currentIndex * 100}%)` }}
          >
            {slides.map((slide) => (
              <img
                key={slide.id}
                src={slide.image}
                alt={`Slide ${slide.id}`}
                className="w-full h-64 object-cover"
              />
            ))}
          </div>
          {/* Navigation buttons */}
          <button
            onClick={() => setCurrentIndex((currentIndex - 1 + slides.length) % slides.length)}
            className="absolute top-1/2 left-2 transform -translate-y-1/2 bg-gray-800 text-white p-2 rounded-full opacity-75 hover:opacity-100"
          >
            &#8249;
          </button>
          <button
            onClick={() => setCurrentIndex((currentIndex + 1) % slides.length)}
            className="absolute top-1/2 right-2 transform -translate-y-1/2 bg-gray-800 text-white p-2 rounded-full opacity-75 hover:opacity-100"
          >
            &#8250;
          </button>
        </div>
      </div>
    </div>
  );
}

export default Carousel;
